class Solution {
public:
    int mod=1e9+7;
    long helper(int n, vector<long>& dp){
        if(n==1) return 1;
        if(n==2) return 2;
        if(n==3) return 5;

        if(dp[n]!=-1) return dp[n]%mod;

        long smallAns1=0, smallAns2=0;
