        unordered_map<int, int> cur;
        return helper(nums, k, cur, 0);
    }
private:
    int helper(vector<int>& nums, int k, unordered_map<int, int>& cur, int start) {
        int cnt = 0;
        for (int i = start; i < nums.size(); ++i) {
            if (cur[nums[i] - k] || cur[nums[i] + k])
                continue;
            cur[nums[i]]++;
            cnt += 1 + helper(nums, k, cur, i + 1);
            cur[nums[i]]--;
        }
        return cnt;
    }
};
